import pandas as pd import os import matplotlib.pyplot as pltos.chdir("/Users/paulbenjamins/Desktop/data_afm")df = pd.read_csv("factors.csv")df = df.drop(columns=(["Unnamed: 0","HML_Europe","HML_Global","HML_US","SMB_Europe","SMB_Global","SMB_US","UMD_Europe","UMD_Global","UMD_US","D.Recession","P.Recession"]))#df.to_excel("/Users/paulbenjamins/Desktop/data_afm/factors.xlsx")#Sharpe Ratio Betrachtung , far away from finishedstd_BAB_Global = df["BAB_Global"].std()std_Mkt_Global = df["Mkt_Global"].std()std_BAB_Europe = df["BAB_US"].std()std_Mkt_Europe = df["Mkt_US"].std()std_BAB_US= df["BAB_Europe"].std()std_Mkt_US = df["Mkt_Europe"].std()#cumulative prod first idea, x axis is off , tbf, x axis should show the date but since the mdate values are currently strings there is an issue#GLOBALGlobal_BAB = 1 + df["BAB_Global"].to_frame()Global_Mkt = 1 + df["Mkt_Global"].to_frame()bab_cum_g = Global_BAB["BAB_Global"].cumprod().to_frame()bab_cum_g["time"] = df["mdate"]bab_cum_g.set_index("time")mkt_cum_g = Global_Mkt["Mkt_Global"].cumprod().to_frame()mkt_cum_g["time"] = df["mdate"]mkt_cum_g.set_index("time")mkt_cum_g.plot(bab_cum_g,label="BAB")#plt.plot(mkt_cum_g,label="Market")plt.legend()plt.show()#USUS_BAB = 1 + df["BAB_US"]US_Mkt = 1 + df["Mkt_US"]bab_cum_us = US_BAB.cumprod()mkt_cum_us = US_Mkt.cumprod()plt.plot(bab_cum_us,label="BAB")plt.plot(mkt_cum_us,label="Market")plt.legend()plt.show()#EUROPEEurope_BAB = 1 + df["BAB_Europe"]Europe_Mkt = 1 + df["Mkt_Europe"]bab_cum_eu = Europe_BAB.cumprod()mkt_cum_eu = Europe_Mkt.cumprod()plt.plot(bab_cum_eu,label="BAB")plt.plot(mkt_cum_eu,label="Market")plt.legend()plt.show()#ONLY BABplt.plot(bab_cum_g,label="GLOBAL")plt.plot(bab_cum_us,label="US")plt.plot(bab_cum_eu,label="EUROPE")plt.legend()plt.show()